// Importing the useState hook from React for state management within components.
import { useState } from "react";

// Sample content to be used within the tabbed interface.
const content = [
  {
    summary: "React is a library for building UIs",
    details:
      "Dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.",
  },
  {
    summary: "State management is like giving state a home",
    details:
      "Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.",
  },
  {
    summary: "We can think of props as the component API",
    details:
      "Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.",
  },
];

/**
 * App component serves as the root component for the tabbed application.
 * It renders the Tabbed component and passes the content as props.
 */
export default function App() {
  return (
    <div>
      <Tabbed content={content} />
    </div>
  );
}

/**
 * Tabbed component responsible for rendering the tabs and the content.
 * @param {Object[]} content - Array of objects containing summary and details for each tab.
 */
function Tabbed({ content }) {
  const [activeTab, setActiveTab] = useState(0); // State to track the active tab.

  return (
    <div>
      <div className="tabs">
        {content.map((_, idx) => (
          <Tab key={idx} num={idx} activeTab={activeTab} onClick={setActiveTab} />
        ))}
      </div>

      {activeTab <= 2 ? (
        <TabContent item={content[activeTab]} />
      ) : (
        <DifferentContent />
      )}
    </div>
  );
}

/**
 * Tab component rendering individual tabs.
 * @param {number} num - The index of the tab.
 * @param {number} activeTab - Index of the currently active tab.
 * @param {Function} onClick - Handler to set the active tab.
 */
function Tab({ num, activeTab, onClick }) {
  return (
    <button
      className={activeTab === num ? "tab active" : "tab"}
      onClick={() => onClick(num)}
    >
      Tab {num + 1}
    </button>
  );
}

/**
 * TabContent component renders the content of a tab.
 * @param {Object} item - The content item containing summary and details.
 */
function TabContent({ item }) {
  const [showDetails, setShowDetails] = useState(true); // State to toggle visibility of details.
  const [likes, setLikes] = useState(0); // State to track likes.

  function handleInc() {
    setLikes(likes + 1); // Increment likes.
  }

  return (
    <div className="tab-content">
      <h4>{item.summary}</h4>
      {showDetails && <p>{item.details}</p>}

      <div className="tab-actions">
        <button onClick={() => setShowDetails((h) => !h)}>
          {showDetails ? "Hide" : "Show"} details
        </button>

        <div className="hearts-counter">
          <span>{likes} ‚ù§Ô∏è</span>
          <button onClick={handleInc}>Like</button>
        </div>
      </div>
    </div>
  );
}

/**
 * DifferentContent component to display alternative content for non-existent tabs.
 */
function DifferentContent() {
  return (
    <div className="tab-content">
      <h4>I'm a DIFFERENT tab, so I reset state üí£üí•</h4>
    </div>
  );
}
